package com.syntax.class09;

public class Array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		char[] abc = new char[6];

		abc[0] = 'A';
		abc[1] = 'B';
		abc[2] = 'C';
		abc[3] = 'D';
		abc[4] = 'E';
		abc[5] = 'F';

		System.out.println(abc[1]);
		
		String[] cars = {"BMW", "Opel", "Mers"};
		System.out.println(cars.length);

	}

}
